import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus } from '@fortawesome/free-solid-svg-icons';
import './Board.css';
import Task from './Task'; // Import der Task-Komponente

const Board = () => {
  const [tasks, setTasks] = useState({
    todo: [
      { id: 1, title: 'Task 1', content: 'Lorem ipsum dolor sit amet.', assignedTo: [] },
      { id: 2, title: 'Task 2', content: 'Consectetur adipiscing elit.', assignedTo: [] },
    ],
    inProgress: [
      { id: 3, title: 'Task 3', content: 'Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.', assignedTo: [] },
    ],
    awaitingFeedback: [
      { id: 6, title: 'Task 6', content: 'Ut enim ad minim veniam.', assignedTo: [] },
    ],
    done: [
      { id: 4, title: 'Task 4', content: 'Quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.', assignedTo: [] },
      { id: 5, title: 'Task 5', content: 'Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.', assignedTo: [] },
    ],
  });

  const [showTask, setShowTask] = useState(false);

  const toggleTask = () => {
    setShowTask(!showTask);
  };

  const assignContactToTask = (taskId, contact) => {
    const updatedTasks = { ...tasks };
    const taskCategory = Object.keys(updatedTasks).find(category =>
      updatedTasks[category].find(task => task.id === taskId)
    );

    if (taskCategory) {
      const taskIndex = updatedTasks[taskCategory].findIndex(task => task.id === taskId);
      if (taskIndex !== -1) {
        updatedTasks[taskCategory][taskIndex].assignedTo.push(contact);
        setTasks(updatedTasks);
      }
    }
  };

  // Beispiel-Kontakte
  const contacts = [
    { id: 1, initials: 'AB', color: '#FFC107' },
    { id: 2, initials: 'CD', color: '#4CAF50' },
    { id: 3, initials: 'EF', color: '#2196F3' },
    { id: 4, initials: 'GH', color: '#FF5722' },
  ];

  // Beispiel: Einigen Tasks Kontakte zuweisen
  assignContactToTask(1, contacts[0]);
  assignContactToTask(1, contacts[1]);
  assignContactToTask(2, contacts[2]);

  return (
    <div className="kanban-board">
      <div className="board-column">
        <div className="add-button" onClick={toggleTask}>
          <FontAwesomeIcon icon={faPlus} className="navbar-icon" />
          <p className="add-text">Add</p>
        </div>
        <h3 className="column-title">To Do</h3>
        <ul className="task-list">
          {tasks.todo.map(task => (
            <li key={task.id} className="task">
              <div className="task-title">
                {task.title}
                <button className="priority-button low">Low</button>
              </div>
              <div className="task-content">{task.content}</div>
              <div className="assigned-contacts">
                {task.assignedTo.map(contact => (
                  <div key={contact.id} className="contact-initial" style={{ backgroundColor: contact.color }}>
                    {contact.initials}
                  </div>
                ))}
              </div>
            </li>
          ))}
        </ul>
      </div>
      <div className="board-column">
        <div className="add-button" onClick={toggleTask}>
          <FontAwesomeIcon icon={faPlus} className="navbar-icon" />
          <p className="add-text">Add</p>
        </div>
        <h3 className="column-title">In Progress</h3>
        <ul className="task-list">
          {tasks.inProgress.map(task => (
            <li key={task.id} className="task">
              <div className="task-title">
                {task.title}
                <button className="priority-button medium">Medium</button>
              </div>
              <div className="task-content">{task.content}</div>
              <div className="assigned-contacts">
                {task.assignedTo.map(contact => (
                  <div key={contact.id} className="contact-initial" style={{ backgroundColor: contact.color }}>
                    {contact.initials}
                  </div>
                ))}
              </div>
            </li>
          ))}
        </ul>
      </div>
      <div className="board-column">
        <div className="add-button" onClick={toggleTask}>
          <FontAwesomeIcon icon={faPlus} className="navbar-icon" />
          <p className="add-text">Add</p>
        </div>
        <h3 className="column-title">Awaiting Feedback</h3>
        <ul className="task-list">
          {tasks.awaitingFeedback.map(task => (
            <li key={task.id} className="task">
              <div className="task-title">
                {task.title}
                <button className="priority-button urgent">High</button>
              </div>
              <div className="task-content">{task.content}</div>
              <div className="assigned-contacts">
                {task.assignedTo.map(contact => (
                  <div key={contact.id} className="contact-initial" style={{ backgroundColor: contact.color }}>
                    {contact.initials}
                  </div>
                ))}
              </div>
            </li>
          ))}
        </ul>
      </div>
      <div className="board-column">
        <div className="add-button" onClick={toggleTask}>
          <FontAwesomeIcon icon={faPlus} className="navbar-icon" />
          <p className="add-text">Add</p>
        </div>
        <h3 className="column-title">Done</h3>
        <ul className="task-list">
          {tasks.done.map(task => (
            <li key={task.id} className="task">
              <div className="task-title">
                {task.title}
                <button className="priority-button low">Low</button>
              </div>
              <div className="task-content">{task.content}</div>
              <div className="assigned-contacts">
                {task.assignedTo.map(contact => (
                  <div key={contact.id} className="contact-initial" style={{ backgroundColor: contact.color }}>
                    {contact.initials}
                  </div>
                ))}
              </div>
            </li>
          ))}
        </ul>
      </div>
      <Task show={showTask} onClose={toggleTask} assignContactToTask={assignContactToTask} />
    </div>
  );
};

export default Board;
