import React, { useState, useEffect } from 'react';
import './LoginForm.css';
import RegistrationForm from './RegistrationForm';
import { useNavigate } from 'react-router-dom';

function LoginForm({ onLogin }) {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [showRegistration, setShowRegistration] = useState(false);
    const [logoShrink, setLogoShrink] = useState(false);

    const navigate = useNavigate();

    // Prüfen, ob ein gültiger Token im Local Storage vorhanden ist
    useEffect(() => {
        const token = localStorage.getItem('access_token');
        if (token) {
            // Hier könntest du weitere Aktionen ausführen, z.B. Profildaten laden
            console.log('User is logged in');
            navigate('/'); // Weiterleitung zur Startseite oder Profilseite
        } else {
            console.log('User is not logged in');
        }
    }, [navigate]);

    useEffect(() => {
        const timer = setTimeout(() => {
            setLogoShrink(true);
        }, 2000); // 2 Sekunden warten, bevor die Animation beginnt

        return () => clearTimeout(timer); // Timer bereinigen, wenn der Effekt nicht mehr benötigt wird
    }, []);


    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Email:', email); // Debugging
        console.log('Password:', password); // Debugging

        fetch('http://localhost:8000/api/login/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ email, password }),
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                return response.json();
            })
            .then(data => {
                console.log('Server response:', data); // Debugging
                if (data.access) {
                    localStorage.setItem('access_token', data.access);
                    onLogin();
                    navigate('/'); // Erfolgreicher Login, Weiterleitung zur Startseite oder Profilseite
                } else {
                    console.error('Login failed, no access token returned');
                }
            })
            .catch((error) => {
                console.error('Fetch error:', error); // Hier werden Fetch-Fehler behandelt
                console.error('Error message:', error.message); // Zeigt die spezifische Fehlermeldung an
            });
    };

    const handleSignUpClick = () => {
        setShowRegistration(true);
    };

    const handleRegistrationSuccess = () => {
        setShowRegistration(false); // Schließe das Registrierungsformular nach erfolgreicher Registrierung
    };



    return (
        <div className="login-page">
                          {!showRegistration && (
            <div className="login-container">
                 <div className={`header ${logoShrink ? 'shrink' : ''}`}>
                <img src="/images/logo2.png" alt="Task Logo" className="image" />
                 <h1>Task</h1>
                </div>

                <div className="form-container">    
                        <form onSubmit={handleSubmit}>
                            <h2>Login</h2>
                            <input className="login-input" type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="E-Mail" required /><br />
                            <input className="login-input" type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Passwort" required /><br />
                            <button className="login-button" type="submit">Login</button>
                        </form>

                    {!showRegistration && (
                        <p className="register-text">Not registered yet? <button onClick={handleSignUpClick}>Sign up here!</button></p>
                    )}

                </div> 
            </div>
              )}
                 {showRegistration && <RegistrationForm onSuccess={handleRegistrationSuccess} />}

        </div>
    );
}

export default LoginForm;
