import React, { useState, useEffect } from 'react';
import Datetime from 'react-datetime';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTimes } from '@fortawesome/free-solid-svg-icons';
import 'react-datetime/css/react-datetime.css';
import './Task.css';

const Task = ({ taskToEdit, onSave, contacts }) => {
  const [title, setTitle] = useState(taskToEdit ? taskToEdit.title : '');
  const [description, setDescription] = useState(taskToEdit ? taskToEdit.description : '');
  const [category, setCategory] = useState(taskToEdit ? taskToEdit.category : 'To Do');
  const [assignedContacts, setAssignedContacts] = useState(taskToEdit ? taskToEdit.assignedContacts : []);
  const [dueDate, setDueDate] = useState(taskToEdit ? taskToEdit.dueDate : null);
  const [selectedPriority, setSelectedPriority] = useState(taskToEdit ? taskToEdit.priority : 'medium');
  const [subtaskText, setSubtaskText] = useState('');
  const [subtasks, setSubtasks] = useState(taskToEdit ? taskToEdit.subtasks : []);

  useEffect(() => {
    if (taskToEdit) {
      setTitle(taskToEdit.title);
      setDescription(taskToEdit.description);
      setCategory(taskToEdit.category);
      setAssignedContacts(taskToEdit.assignedContacts);
      setDueDate(taskToEdit.dueDate);
      setSelectedPriority(taskToEdit.priority);
      setSubtasks(taskToEdit.subtasks);
    }
  }, [taskToEdit]);

  const handleAddAssignedContact = contactId => {
    const contact = contacts.find(c => c.id === contactId);
    if (contact && !assignedContacts.some(ac => ac.id === contactId)) {
      setAssignedContacts([...assignedContacts, contact]);
    }
  };

  const handleRemoveAssignedContact = contactId => {
    setAssignedContacts(assignedContacts.filter(ac => ac.id !== contactId));
  };

  const handleSelectPriority = priority => {
    setSelectedPriority(priority);
  };

  const handleAddSubtask = () => {
    if (subtaskText.trim()) {
      setSubtasks([...subtasks, { text: subtaskText, completed: false }]);
      setSubtaskText('');
    }
  };

  const handleSubmit = event => {
    event.preventDefault();
    onSave({
      title,
      description,
      category,
      assignedContacts,
      dueDate,
      priority: selectedPriority,
      subtasks
    });
  };

  return (
    <div className="task-form">
      <form onSubmit={handleSubmit}>
        <div className="form-left">
          <label>Title</label>
          <input type="text" value={title} onChange={e => setTitle(e.target.value)} />
          <label>Description</label>
          <textarea value={description} onChange={e => setDescription(e.target.value)} />
          <label>Category</label>
          <select value={category} onChange={e => setCategory(e.target.value)}>
            <option value="To Do">To Do</option>
            <option value="In Progress">In Progress</option>
            <option value="Done">Done</option>
          </select>
          <label>Assigned to</label>
          <select onChange={e => handleAddAssignedContact(e.target.value)}>
            <option value="">Select a contact</option>
            {contacts.map(contact => (
              <option key={contact.id} value={contact.id}>
                {contact.first_name} {contact.last_name}
              </option>
            ))}
          </select>
          <div className="assigned-contacts">
            {assignedContacts.map(contact => (
              <div
                key={contact.id}
                className="assigned-contact"
                style={{ backgroundColor: contact.color }}
              >
                {contact.first_name} {contact.last_name}
                <button type="button" onClick={() => handleRemoveAssignedContact(contact.id)}>
                  <FontAwesomeIcon icon={faTimes} />
                </button>
              </div>
            ))}
          </div>
          <label>Due Date</label>
          <Datetime value={dueDate} onChange={setDueDate} />
        </div>
        <div className="form-right">
          <label>Priority</label>
          <div className="priority-buttons">
            <button
              type="button"
              className={selectedPriority === 'low' ? 'active' : ''}
              onClick={() => handleSelectPriority('low')}
            >
              Low
            </button>
            <button
              type="button"
              className={selectedPriority === 'medium' ? 'active' : ''}
              onClick={() => handleSelectPriority('medium')}
            >
              Medium
            </button>
            <button
              type="button"
              className={selectedPriority === 'high' ? 'active' : ''}
              onClick={() => handleSelectPriority('high')}
            >
              High
            </button>
          </div>
          <label>Subtasks</label>
          <div className="subtask-input">
            <input
              type="text"
              value={subtaskText}
              onChange={e => setSubtaskText(e.target.value)}
              placeholder="Enter subtask"
            />
            <button type="button" onClick={handleAddSubtask}>
              Add Subtask
            </button>
          </div>
          <ul className="subtasks-list">
            {subtasks.map((subtask, index) => (
              <li key={index}>
                <input
                  type="checkbox"
                  checked={subtask.completed}
                  onChange={() => {
                    const updatedSubtasks = [...subtasks];
                    updatedSubtasks[index].completed = !updatedSubtasks[index].completed;
                    setSubtasks(updatedSubtasks);
                  }}
                />
                {subtask.text}
              </li>
            ))}
          </ul>
        </div>
        <div className="form-footer">
          <button type="submit">{taskToEdit ? 'Save Changes' : 'Create Task'}</button>
        </div>
      </form>
    </div>
  );
};

export default Task;
