import React from 'react';

const TaskDetailsPopup = ({ task, onClose }) => {
  if (!task) return null;

  // Funktion zur Formatierung des Datums
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString(); // Gibt nur das Datum im lokalen Format zurück
  };

  return (
    <div className="task-details-popup">
      <div className="popup-content">
        <button className="close-button" onClick={onClose}>&times;</button>
        <h2>{task.title}</h2>
        <p>Description: {task.description}</p>
        <p>Category: {task.category}</p>
        {task.due_date && <p>Due Date: {formatDate(task.due_date)}</p>}
        {task.priority && <p>Priority: {task.priority}</p>}
        {task.subtasks.length > 0 && (
          <div>
            <p>Subtasks:</p>
            <ul>
              {task.subtasks.map((subtask, index) => (
                <li key={index} className={subtask.completed ? 'completed' : ''}>
                  {subtask.text}
                </li>
              ))}
            </ul>
          </div>
        )}
        {task.contacts.length > 0 && (
          <div>
            <p>Assigned Contacts:</p>
            <ul>
              {task.contacts.map(contact => (
                <li key={contact.id}>
                  {contact.name}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default TaskDetailsPopup;
