import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTimes } from '@fortawesome/free-solid-svg-icons';
import Datetime from 'react-datetime';
import 'react-datetime/css/react-datetime.css';

import './Task.css'; // Import der CSS-Datei

const Task = ({ show, onClose }) => {
    const categories = [
        { id: 1, name: 'To Do' },
        { id: 2, name: 'In Progress' },
        { id: 3, name: 'Awaiting Feedback' },
        { id: 4, name: 'Done' }
    ];

    const [title, setTitle] = useState(''); // State für Titel
    const [description, setDescription] = useState(''); // State für Beschreibung
    const [category, setCategory] = useState(''); // State für Kategorie
    const [dueDate, setDueDate] = useState(null); // State für Fälligkeitsdatum
    const [subtaskText, setSubtaskText] = useState(''); // State für Subtask-Text
    const [subtasks, setSubtasks] = useState([]); // State für Subtasks
    const [assignedContacts, setAssignedContacts] = useState([]); // State für zugewiesene Kontakte
    const [selectedPriority, setSelectedPriority] = useState(null); // State für ausgewählte Priorität
    const [contactColors, setContactColors] = useState({}); // State für gespeicherte Kontakt-Farben

    // Dummy-Kontakte (später durch echte Kontakte ersetzen)
    const dummyContacts = [
        { id: 1, name: 'John Doe' },
        { id: 2, name: 'Jane Smith' },
        { id: 3, name: 'Michael Johnson' },
        { id: 4, name: 'Emily Davis' },
        { id: 5, name: 'David Brown' }
    ];

    useEffect(() => {
        assignColorsToContacts();
    }, []); // Empty dependency array ensures this runs once on component mount

    // Funktion, um Farben einmalig zuzuweisen
    const assignColorsToContacts = () => {
        const colors = ['#007bff', '#28a745', '#dc3545', '#ffc107', '#17a2b8']; // Farben zur Auswahl
        const usedColors = {};
        const updatedContacts = dummyContacts.map(contact => {
            if (contactColors[contact.id]) {
                return { ...contact, color: contactColors[contact.id] };
            } else {
                let colorIndex = 0;
                let chosenColor = colors[colorIndex];
                while (usedColors[chosenColor]) {
                    colorIndex++;
                    chosenColor = colors[colorIndex];
                }
                usedColors[chosenColor] = true;
                setContactColors(prevColors => ({
                    ...prevColors,
                    [contact.id]: chosenColor
                }));
                return { ...contact, color: chosenColor };
            }
        });
        setAssignedContacts(updatedContacts);
    };

    const handleAddSubtask = () => {
        if (subtaskText.trim()) {
            setSubtasks([...subtasks, { text: subtaskText, completed: false }]);
            setSubtaskText('');
        }
    };

    const handleToggleSubtask = index => {
        const newSubtasks = subtasks.map((subtask, i) =>
            i === index ? { ...subtask, completed: !subtask.completed } : subtask
        );
        setSubtasks(newSubtasks);
    };

    const handleAssignContact = event => {
        const selectedContactId = parseInt(event.target.value);
        const selectedContact = dummyContacts.find(contact => contact.id === selectedContactId);

        if (selectedContact) {
            setAssignedContacts([...assignedContacts, selectedContact]);
        }
    };

    const handleRemoveAssignedContact = contactId => {
        const updatedContacts = assignedContacts.filter(contact => contact.id !== contactId);
        setAssignedContacts(updatedContacts);
    };

    const handleSelectPriority = priority => {
        setSelectedPriority(priority);
    };

    const handleClearAll = () => {
        setTitle('');
        setDescription('');
        setCategory('');
        setDueDate(null);
        setSubtaskText('');
        setSubtasks([]);
        setAssignedContacts([]);
        setSelectedPriority(null);
        setContactColors({});
        assignColorsToContacts();
    };

    return (
        <div className={`task-container ${show ? 'show' : ''}`}>
            <div className="task-header">
                <h2>Add Task</h2>
                <button className="close-button" onClick={onClose}>
                    <FontAwesomeIcon icon={faTimes} />
                </button>
            </div>
            <form className="task-form">
                <div className="form-left">
                    <label>Title</label>
                    <input
                        type="text"
                        value={title}
                        onChange={e => setTitle(e.target.value)}
                        placeholder="Enter a title"
                    />
                    <label>Description</label>
                    <textarea
                        value={description}
                        onChange={e => setDescription(e.target.value)}
                        placeholder="Enter a description"
                    ></textarea>
                    <label>Category</label>
                    <select
                        value={category}
                        onChange={e => setCategory(e.target.value)}
                    >
                        <option value="">Select task category</option>
                        {categories.map(category => (
                            <option key={category.id} value={category.id}>
                                {category.name}
                            </option>
                        ))}
                    </select>
                    <label>Assigned to</label>
                    <select onChange={handleAssignContact}>
                        <option value="">Select contacts to assign</option>
                        {dummyContacts.map(contact => (
                            <option key={contact.id} value={contact.id}>
                                {contact.name}
                            </option>
                        ))}
                    </select>
                    <ul className="assigned-contacts">
                        {assignedContacts.map(contact => (
                            <li key={contact.id}>
                                <div className="contact-info">
                                    <div
                                        className="contact-initials"
                                        style={{ backgroundColor: contact.color }}
                                    >
                                        {contact.name
                                            .split(' ')
                                            .map(part => part.charAt(0))
                                            .join('')
                                            .toUpperCase()}
                                    </div>
                                    <span>{contact.name}</span>
                                </div>
                                <button
                                    type="button"
                                    className="remove-contact"
                                    onClick={() => handleRemoveAssignedContact(contact.id)}
                                >
                                    <FontAwesomeIcon icon={faTimes} />
                                </button>
                            </li>
                        ))}
                    </ul>
                </div>
                <div className="form-right">
                    <label>Due date</label>
                    <Datetime
                        value={dueDate}
                        onChange={date => setDueDate(date)}
                        dateFormat="DD/MM/YYYY"
                        timeFormat={false}
                        inputProps={{ placeholder: 'Select due date' }}
                    />
                    <label>Prio</label>
                    <div className="prio-buttons">
                        <button
                            type="button"
                            className={`prio-button urgent ${selectedPriority === 'urgent' ? 'selected' : ''}`}
                            onClick={() => handleSelectPriority('urgent')}
                        >
                            Urgent
                        </button>
                        <button
                            type="button"
                            className={`prio-button medium ${selectedPriority === 'medium' ? 'selected' : ''}`}
                            onClick={() => handleSelectPriority('medium')}
                        >
                            Medium
                        </button>
                        <button
                            type="button"
                            className={`prio-button low ${selectedPriority === 'low' ? 'selected' : ''}`}
                            onClick={() => handleSelectPriority('low')}
                        >
                            Low
                        </button>
                    </div>
                    <label>Subtasks</label>
                    <div className="subtask-input">
                        <input
                            type="text"
                            value={subtaskText}
                            onChange={e => setSubtaskText(e.target.value)}
                            placeholder="Add new subtask"
                        />
                        <button type="button" onClick={handleAddSubtask}>
                            Add
                        </button>
                    </div>
                    <ul className="subtask-list">
                        {subtasks.map((subtask, index) => (
                            <li key={index} className={subtask.completed ? 'completed' : ''}>
                                <input
                                    type="checkbox"
                                    checked={subtask.completed}
                                    onChange={() => handleToggleSubtask(index)}
                                />
                                <span>{subtask.text}</span>
                            </li>
                        ))}
                    </ul>
                </div>
            </form>
            <div className="form-footer">
                <button type="button" className="clear-button" onClick={handleClearAll}>
                    Clear
                </button>
                <button type="submit" className="create-button">
                    Create Task
                </button>
            </div>
        </div>
    );
};

export default Task;
