import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { useNavigate } from 'react-router-dom';
import { faTimes } from '@fortawesome/free-solid-svg-icons';
import Datetime from 'react-datetime';
import 'react-datetime/css/react-datetime.css';
import './Task.css';

const Task = ({ show, onClose, createTask, editTask, taskToEdit, onTaskUpdate }) => {
  const navigate = useNavigate();

  const categories = [
    { id: 'todo', name: 'To Do' },
    { id: 'inProgress', name: 'In Progress' },
    { id: 'awaitingFeedback', name: 'Awaiting Feedback' },
    { id: 'done', name: 'Done' }
  ];

  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [category, setCategory] = useState('');
  const [dueDate, setDueDate] = useState(null);
  const [subtaskText, setSubtaskText] = useState('');
  const [subtasks, setSubtasks] = useState([]);
  const [assignedContacts, setAssignedContacts] = useState([]);
  const [selectedPriority, setSelectedPriority] = useState(null);
  const [formErrors, setFormErrors] = useState({
    title: false,
    description: false,
    category: false
  });

  const [users, setUsers] = useState([]);
  const [usedColors, setUsedColors] = useState({});
  const [selectedContacts, setSelectedContacts] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8000/api/users/')
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const contentType = response.headers.get('content-type');
        if (contentType && contentType.indexOf('application/json') !== -1) {
          return response.json();
        } else {
          throw new Error('Received non-JSON response');
        }
      })
      .then(data => {
        setUsers(data);
      })
      .catch(error => {
        console.error('Fetch error:', error);
      });
  }, []);

  useEffect(() => {
    if (taskToEdit) {
      setTitle(taskToEdit.title);
      setDescription(taskToEdit.description);
      setCategory(taskToEdit.category);
      setDueDate(taskToEdit.due_date ? new Date(taskToEdit.due_date) : null);
      setSubtasks(taskToEdit.subtasks || []);
      setAssignedContacts(taskToEdit.contacts || []);
      setSelectedPriority(taskToEdit.priority || null);
      setSelectedContacts(taskToEdit.contacts || []);
      const colors = {};
      taskToEdit.contacts.forEach(contact => {
        colors[contact.color] = true;
      });
      setUsedColors(colors);
    }
  }, [taskToEdit]);

  const handleAddSubtask = () => {
    if (subtaskText.trim()) {
      setSubtasks([...subtasks, { text: subtaskText, completed: false }]);
      setSubtaskText('');
    }
  };

  const handleAssignContact = event => {
    const selectedContactId = parseInt(event.target.value);
    const selectedContact = users.find(user => user.id === selectedContactId);

    if (selectedContact && !assignedContacts.some(contact => contact.id === selectedContact.id)) {
      setAssignedContacts([...assignedContacts, selectedContact]);
      setSelectedContacts([...selectedContacts, selectedContact]);
      setUsedColors({ ...usedColors, [selectedContact.color]: true });
    }
  };

  const handleRemoveAssignedContact = contactId => {
    const removedContact = assignedContacts.find(contact => contact.id === contactId);
    if (removedContact) {
      const { color } = removedContact;
      const updatedContacts = assignedContacts.filter(contact => contact.id !== contactId);
      setAssignedContacts(updatedContacts);
      setUsedColors({ ...usedColors, [color]: false });
      setSelectedContacts(selectedContacts.filter(contact => contact.id !== contactId));
    }
  };

  const handleSelectPriority = priority => {
    setSelectedPriority(priority);
  };

  const handleClearAll = () => {
    setTitle('');
    setDescription('');
    setCategory('');
    setDueDate(null);
    setSubtaskText('');
    setSubtasks([]);
    setAssignedContacts([]);
    setSelectedPriority(null);
    setUsedColors({});
    setFormErrors({
        title: false,
        description: false,
        category: false
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    let errors = {};

    if (!title.trim()) {
      errors = { ...errors, title: true };
    }
    if (!description.trim()) {
      errors = { ...errors, description: true };
    }
    if (!category) {
      errors = { ...errors, category: true };
    }

    if (Object.keys(errors).length > 0) {
      setFormErrors(errors);
      return;
    }

    const taskData = {
      title,
      description,
      category,
      due_date: dueDate,
      priority: selectedPriority,
      subtasks: subtasks.map(subtask => ({
          text: subtask.text,
          completed: subtask.completed
      })),
      contacts: assignedContacts.map(contact => ({
        id: contact.id,
        first_name: contact.first_name,
        last_name: contact.last_name,
        color: contact.color
      }))
    };

    const requestMethod = taskToEdit ? 'PUT' : 'POST';
    const url = taskToEdit ? `http://localhost:8000/api/tasks/${taskToEdit.id}/` : 'http://localhost:8000/api/tasks/';

    fetch(url, {
      method: requestMethod,
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(taskData),
    })
    .then(response => {
      if (!response.ok) {
        return response.json().then(errorData => {
          console.error('Error response:', errorData);
          throw new Error(`HTTP error! status: ${response.status}`);
        });
      }
      return response.json();
    })
    .then(data => {
      handleClearAll();
      onClose();
      onTaskUpdate(); // Notify parent component about the update
      navigate('/board'); // Redirect to the board
    })
    .catch((error) => {
      console.error('Fetch error:', error);
    });
  };

  return (
    <div className={`task-container ${show ? 'show' : ''}`}>
      <div className="popup-content">
        <button className="close-button" onClick={() => { onClose(); navigate('/board'); }}>&times;</button>
        <div className="task-title-popup">
          <h2>{taskToEdit ? 'Edit Task' : 'Add Task'}</h2>
        </div>
      </div>
      <form className="task-form" onSubmit={handleSubmit}>
        <div className="form-left">
          <label>Title</label>
          <input
            type="text"
            value={title}
            onChange={e => setTitle(e.target.value)}
            placeholder="Enter a title"
            className={formErrors.title ? 'error' : ''}
          />
          {formErrors.title && <div className="error-text">This field is required</div>}
          <label>Description</label>
          <textarea
            value={description}
            onChange={e => setDescription(e.target.value)}
            placeholder="Enter a description"
            className={formErrors.description ? 'error' : ''}
          ></textarea>
          {formErrors.description && <div className="error-text">This field is required</div>}
          <label>Category</label>
          <select
            value={category}
            onChange={e => setCategory(e.target.value)}
            className={formErrors.category ? 'error' : ''}
          >
            <option value="">Select task category</option>
            {categories.map(category => (
              <option key={category.id} value={category.id}>
                {category.name}
              </option>
            ))}
          </select>
          {formErrors.category && <div className="error-text">This field is required</div>}
          <label>Assigned to</label>
          <select onChange={handleAssignContact}>
            <option value="">Select contacts to assign</option>
            {users.map(user => (
              <option key={user.id} value={user.id} disabled={selectedContacts.some(c => c.id === user.id)}>
                {user.first_name} {user.last_name}
              </option>
            ))}
          </select>
          <ul className="assigned-contacts">
            {assignedContacts.map(contact => (
              <li key={contact.id}>
                <div className="contact-info-task">
                  <div
                    className="contact-initials"
                    style={{ backgroundColor: contact.color  }}
                  >
                    {contact.first_name.charAt(0).toUpperCase()}
                    {contact.last_name.charAt(0).toUpperCase()}
                  </div>
                  <div>{contact.first_name} {contact.last_name}</div>
                  <button type="button" onClick={() => handleRemoveAssignedContact(contact.id)}>Remove</button>
                </div>
              </li>
            ))}
          </ul>
        </div>
        <div className="form-right">
          <label>Due Date</label>
          <Datetime
            value={dueDate}
            onChange={date => setDueDate(date.toDate())}
            timeFormat={false}
          />
          <label>Priority</label>
          <div className="priority-buttons">
            <button
              type="button"
              className={`priority-button ${selectedPriority === 'low' ? 'selected' : ''}`}
              onClick={() => handleSelectPriority('low')}
            >
              Low
            </button>
            <button
              type="button"
              className={`priority-button ${selectedPriority === 'medium' ? 'selected' : ''}`}
              onClick={() => handleSelectPriority('medium')}
            >
              Medium
            </button>
            <button
              type="button"
              className={`priority-button ${selectedPriority === 'high' ? 'selected' : ''}`}
              onClick={() => handleSelectPriority('high')}
            >
              High
            </button>
          </div>
          <label>Subtasks</label>
          <div className="subtask-container">
            <input
              type="text"
              value={subtaskText}
              onChange={e => setSubtaskText(e.target.value)}
              placeholder="Enter a subtask"
            />
            <button type="button" onClick={handleAddSubtask}>Add Subtask</button>
            <ul className="subtasks-list">
              {subtasks.map((subtask, index) => (
                <li key={index}>
                  <input
                    type="checkbox"
                    checked={subtask.completed}
                    onChange={() => {
                      const updatedSubtasks = subtasks.map((t, i) =>
                        i === index ? { ...t, completed: !t.completed } : t
                      );
                      setSubtasks(updatedSubtasks);
                    }}
                  />
                  {subtask.text}
                </li>
              ))}
            </ul>
          </div>
        </div>
        <button type="submit">{taskToEdit ? 'Update Task' : 'Create Task'}</button>
      </form>
    </div>
  );
};

export default Task;
