import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import './Summary.css';

const Summary = () => {
  const [date, setDate] = useState(new Date());
  const [summary, setSummary] = useState(null);

  useEffect(() => {
    fetch('http://localhost:8000/api/summary/')
      .then(response => response.json())
      .then(data => setSummary(data));
  }, []);

  const getGreeting = () => {
    const currentTime = new Date().getHours();
    let greeting = '';

    if (currentTime < 12) {
      greeting = 'Good Morning';
    } else if (currentTime < 18) {
      greeting = 'Good Afternoon';
    } else {
      greeting = 'Good Evening';
    }

    return greeting;
  };

  if (!summary) {
    return <div>Loading...</div>;
  }

  return (
    <div className="summary-wrapper">
      <div className="summary-greeting">
        <h2>{getGreeting()}, John!</h2>
        <Calendar onChange={setDate} value={date} />
      </div>
      <div className="summary-container">
        <h2>Summary</h2>
        <h4>Everything in a Nutshell</h4>
        <div className="summary-grid">
          <Link to="/board" className="summary-item">
            <span className="summary-number">{summary.total_tasks}</span>
            <span className="summary-label">Tasks in Board</span>
          </Link>
          {summary.tasks_by_status.map(status => (
            <Link to="/board" className="summary-item" key={status.status}>
              <span className="summary-number">{status.count}</span>
              <span className="summary-label">{status.status.replace('_', ' ').capitalize()}</span>
            </Link>
          ))}
          <Link to="/board" className="summary-item urgent">
            <span className="summary-number">{summary.urgent_tasks}</span>
            <span className="summary-label">Urgent</span>
          </Link>
          <Link to="/board" className="summary-item">
            <span className="summary-deadline">{summary.next_deadline}</span>
            <span className="summary-label">Upcoming Deadline</span>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Summary;
