import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus } from '@fortawesome/free-solid-svg-icons';
import './Board.css';
import Task from './Task';
import TaskDetailsPopup from './TaskDetailsPopup';

const Board = () => {
  const [tasks, setTasks] = useState({
    todo: [],
    'In Progress': [],
    'awaiting Feedback': [],
    done: [],
  });

  const [showTask, setShowTask] = useState(false);
  const [selectedTask, setSelectedTask] = useState(null);
  const [isEdit, setIsEdit] = useState(false);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/tasks/');
      if (!response.ok) {
        throw new Error(`HTTP-Fehler! Status: ${response.status}`);
      }
      const data = await response.json();
      const formattedTasks = {
        todo: data.filter(task => task.category === 'todo'),
        'In Progress': data.filter(task => task.category === 'inProgress'),
        'awaiting Feedback': data.filter(task => task.category === 'awaitingFeedback'),
        done: data.filter(task => task.category === 'done'),
      };
      setTasks(formattedTasks);
    } catch (error) {
      console.error('Fehler beim Abrufen der Aufgaben:', error.message);
    }
  };

  const toggleTask = () => {
    setShowTask(!showTask);
  };

  const createTask = async (taskData) => {
    try {
      const response = await fetch('http://localhost:8000/api/tasks/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(taskData),
      });
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const createdTask = await response.json();
      setTasks(prevTasks => ({
        ...prevTasks,
        [createdTask.category]: [...prevTasks[createdTask.category], createdTask],
      }));
      setShowTask(false);
      window.location.reload();
    } catch (error) {
      console.error('Error creating task:', error);
    }
  };

  const openTaskDetails = (task) => {
    setSelectedTask(task);
    setIsEdit(false);
  };

  const closeTaskDetails = () => {
    setSelectedTask(null);
    setShowTask(false);
    setIsEdit(false);
  };

  const handleEditTask = async (editedTask) => {
    try {
      const taskData = {
        title: editedTask.title,
        description: editedTask.description,
        category: editedTask.category,
        due_date: editedTask.due_date,
        priority: editedTask.priority,
        subtasks: editedTask.subtasks.map(subtask => ({
          text: subtask.text,
          completed: subtask.completed,
        })),
        contacts: editedTask.contacts.map(contact => ({
          id: contact.id,
          name: contact.name
        }))
      };

      const response = await fetch(`http://localhost:8000/api/tasks/${editedTask.id}/`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(taskData),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const updatedTask = await response.json();
      setTasks(prevTasks => {
        const updatedTasks = { ...prevTasks };
        updatedTasks[updatedTask.category] = prevTasks[updatedTask.category].map(task =>
          task.id === updatedTask.id ? updatedTask : task
        );
        return updatedTasks;
      });
      setSelectedTask(null);
      setIsEdit(false);
    } catch (error) {
      console.error('Error editing task:', error);
    }
  };

  const handleDeleteTask = (taskId) => {
    fetch(`http://localhost:8000/api/tasks/${taskId}/`, {
      method: 'DELETE',
    })
    .then(response => {
      if (!response.ok) {
        throw new Error(`HTTP-Fehler! Status: ${response.status}`);
      }
      // Update state to reflect the deleted task
      setTasks(prevTasks => {
        const updatedTasks = { ...prevTasks };
        for (const category in updatedTasks) {
          updatedTasks[category] = updatedTasks[category].filter(task => task.id !== taskId);
        }
        return updatedTasks;
      });
      setSelectedTask(null);
    })
    .catch(error => {
      console.error('Fehler beim Löschen der Aufgabe:', error);
    });
  };

  const renderTasks = (taskList) => {
    return taskList.map(task => (
      <li key={task.id} className="task" onClick={() => openTaskDetails(task)}>
        <div className="task-title">
          {task.title}
          <button className={`priority-button ${task.priority?.toLowerCase() || 'low'}`}>
            {task.priority || 'Low'}
          </button>
        </div>
        <div className="task-content">{task.description}</div>
        <div className="assigned-contacts-board">
          {task.contacts && task.contacts.map(contact => (
            <div key={contact.id} className="contact-initials" style={{ backgroundColor: contact.color }}>
              {contact.name
                .split(' ')
                .map(part => part.charAt(0))
                .join('')
                .toUpperCase()}
            </div>
          ))}
        </div>
      </li>
    ));
  };

  return (
    <div className="kanban-board">
      {['todo', 'In Progress', 'awaiting Feedback', 'done'].map(category => (
        <div key={category} className="board-column">
          <div className="add-button" onClick={toggleTask}>
            <FontAwesomeIcon icon={faPlus} className="navbar-icon" />
            <p className="add-text">Add</p>
          </div>
          <h3 className="column-title">{category.replace(/([A-Z])/g, ' $1')}</h3>
          <ul className="task-list">
            {renderTasks(tasks[category])}
          </ul>
        </div>
      ))}
      <TaskDetailsPopup task={selectedTask} onClose={closeTaskDetails} onEdit={() => setIsEdit(true)} onDelete={handleDeleteTask} />
      <Task show={showTask || isEdit} onClose={closeTaskDetails} createTask={createTask} editTask={handleEditTask} taskToEdit={isEdit ? selectedTask : null} />
    </div>
  );
};

export default Board;
