import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from './components/Navbar';
import Board from './components/Board';
import Summary from './components/Summary';
import Task from './components/Task';
import LoginForm from './components/LoginForm'; 

const App = () => {
  const [loggedIn, setLoggedIn] = useState(false); // Zustandsvariable für den Anmeldestatus

  useEffect(() => {


    // Überprüfe beim Laden der App, ob ein gültiger Zugriffstoken im Local Storage vorhanden ist
    const token = localStorage.getItem('access_token');
    if (token) {
      setLoggedIn(true); // Setze den Anmeldestatus entsprechend dem Vorhandensein des Tokens
    }
  });

  const [taskToEdit, setTaskToEdit] = useState(null);

  const handleCloseTask = () => {
    setTaskToEdit(null);
    // Navigate programmatically to '/board' using history object
    window.history.pushState({}, '', '/board');
  };

  const handleEditTask = (task) => {
    setTaskToEdit(task);
    // Navigate programmatically to '/task' using history object
    window.history.pushState({}, '', '/task');
  };

  const handleLogin = () => {
    setLoggedIn(true); // Funktion, um den Anmeldestatus zu aktualisieren
  };

  return (
    <Router>
    <div >
    {!loggedIn ? ( // Zeige das LoginForm, wenn nicht angemeldet
      <LoginForm onLogin={handleLogin} />

    ) : ( 
      <>
      <Navbar />
      <Routes>
        <Route exact path="/" element={<Summary />} />
        <Route exact path="/board" element={<Board onEditTask={handleEditTask} />} />
        <Route
          exact
          path="/task"
          element={
            <Task show={true} onClose={handleCloseTask} taskToEdit={taskToEdit} />
          }
        />
      </Routes>
      </>
          )}
    </div>
    </Router>
  );
}

export default App;
