import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus } from '@fortawesome/free-solid-svg-icons';
import './Board.css';
import Task from './Task';

const Board = () => {
  const [tasks, setTasks] = useState({
    todo: [],
    inProgress: [],
    awaitingFeedback: [],
    done: [],
  });

  const [showTask, setShowTask] = useState(false);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await fetch('/api/tasks'); // assuming the endpoint is /api/tasks
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const data = await response.json();
      const formattedTasks = {
        todo: data.filter(task => task.category === 'todo'),
        inProgress: data.filter(task => task.category === 'inProgress'),
        awaitingFeedback: data.filter(task => task.category === 'awaitingFeedback'),
        done: data.filter(task => task.category === 'done'),
      };
      setTasks(formattedTasks);
    } catch (error) {
      console.error('Error fetching tasks:', error);
      // Log the response text to get more details about the error
      try {
        const errorText = await error.response.text();
        console.error('Error response text:', errorText);
      } catch (err) {
        console.error('Unable to read error response text');
      }
    }
  };

  const toggleTask = () => {
    setShowTask(!showTask);
  };

  const createTask = (title, content, category) => {
    const newTask = {
      id: Date.now(),
      title,
      content,
      assignedTo: [],
    };

    setTasks(prevTasks => ({
      ...prevTasks,
      [category]: [...prevTasks[category], newTask],
    }));
    setShowTask(false);  // Close the Task dialog after creating a task
  };

  return (
    <div className="kanban-board">
      <div className="board-column">
        <div className="add-button" onClick={toggleTask}>
          <FontAwesomeIcon icon={faPlus} className="navbar-icon" />
          <p className="add-text">Add</p>
        </div>
        <h3 className="column-title">To Do</h3>
        <ul className="task-list">
          {tasks.todo.map(task => (
            <li key={task.id} className="task">
              <div className="task-title">
                {task.title}
                <button className="priority-button low">Low</button>
              </div>
              <div className="task-content">{task.description}</div>
              <div className="assigned-contacts">
                {task.contacts && task.contacts.map(contact => (
                  <div key={contact.id} className="contact-initial" style={{ backgroundColor: contact.color }}>
                    {contact.name[0]}
                  </div>
                ))}
              </div>
            </li>
          ))}
        </ul>
      </div>
      <div className="board-column">
        <div className="add-button" onClick={toggleTask}>
          <FontAwesomeIcon icon={faPlus} className="navbar-icon" />
          <p className="add-text">Add</p>
        </div>
        <h3 className="column-title">In Progress</h3>
        <ul className="task-list">
          {tasks.inProgress.map(task => (
            <li key={task.id} className="task">
              <div className="task-title">
                {task.title}
                <button className="priority-button medium">Medium</button>
              </div>
              <div className="task-content">{task.description}</div>
              <div className="assigned-contacts">
                {task.contacts && task.contacts.map(contact => (
                  <div key={contact.id} className="contact-initial" style={{ backgroundColor: contact.color }}>
                    {contact.name[0]}
                  </div>
                ))}
              </div>
            </li>
          ))}
        </ul>
      </div>
      <div className="board-column">
        <div className="add-button" onClick={toggleTask}>
          <FontAwesomeIcon icon={faPlus} className="navbar-icon" />
          <p className="add-text">Add</p>
        </div>
        <h3 className="column-title">Awaiting Feedback</h3>
        <ul className="task-list">
          {tasks.awaitingFeedback.map(task => (
            <li key={task.id} className="task">
              <div className="task-title">
                {task.title}
                <button className="priority-button urgent">High</button>
              </div>
              <div className="task-content">{task.description}</div>
              <div className="assigned-contacts">
                {task.contacts && task.contacts.map(contact => (
                  <div key={contact.id} className="contact-initial" style={{ backgroundColor: contact.color }}>
                    {contact.name[0]}
                  </div>
                ))}
              </div>
            </li>
          ))}
        </ul>
      </div>
      <div className="board-column">
        <div className="add-button" onClick={toggleTask}>
          <FontAwesomeIcon icon={faPlus} className="navbar-icon" />
          <p className="add-text">Add</p>
        </div>
        <h3 className="column-title">Done</h3>
        <ul className="task-list">
          {tasks.done.map(task => (
            <li key={task.id} className="task">
              <div className="task-title">
                {task.title}
                <button className="priority-button low">Low</button>
              </div>
              <div className="task-content">{task.description}</div>
              <div className="assigned-contacts">
                {task.contacts && task.contacts.map(contact => (
                  <div key={contact.id} className="contact-initial" style={{ backgroundColor: contact.color }}>
                    {contact.name[0]}
                  </div>
                ))}
              </div>
            </li>
          ))}
        </ul>
      </div>
      <Task show={showTask} onClose={toggleTask} createTask={createTask} />
    </div>
  );
};

export default Board;
