import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus } from '@fortawesome/free-solid-svg-icons';
import './Board.css';
import Task from './Task';
import TaskDetailsPopup from './TaskDetailsPopup'; 

const Board = () => {
  const [tasks, setTasks] = useState({
    todo: [],
    'In Progress': [], // Umbenannt von inProgress
    'awaiting Feedback': [], // Umbenannt von awaitingFeedback
    done: [],
  });

  const [showTask, setShowTask] = useState(false);
  const [selectedTask, setSelectedTask] = useState(null);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/tasks/');
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const data = await response.json();
      const formattedTasks = {
        todo: data.filter(task => task.category === 'todo'),
        'In Progress': data.filter(task => task.category === 'inProgress'), // Anpassung hier
        'awaiting Feedback': data.filter(task => task.category === 'awaitingFeedback'), // Anpassung hier
        done: data.filter(task => task.category === 'done'),
      };
      setTasks(formattedTasks);
    } catch (error) {
      console.error('Error fetching tasks:', error.message);
      try {
        const errorText = await error.text();
        console.error('Error response text:', errorText);
      } catch (err) {
        console.error('Unable to read error response text');
      }
    }
  };

  const toggleTask = () => {
    setShowTask(!showTask);
  };

  const createTask = (title, content, category) => {
    const newTask = {
      id: Date.now(),
      title,
      content,
      category,
      contacts: [],
    };

    setTasks(prevTasks => ({
      ...prevTasks,
      [category]: [newTask, ...prevTasks[category]],
    }));
    setShowTask(false);
  };

  const openTaskDetails = (task) => {
    setSelectedTask(task);
  };

  const closeTaskDetails = () => {
    setSelectedTask(null);
  };

  const handleEditTask = (task) => {
    // Hier sollte die Logik zur Bearbeitung eines Tasks stehen
    console.log('Edit task:', task);
  };

  const renderTasks = (taskList) => {
    return taskList.map(task => (
       <li key={task.id} className="task" onClick={() => openTaskDetails(task)}>
        <div className="task-title">
          {task.title}
          <button className={`priority-button ${task.priority?.toLowerCase() || 'low'}`}>
            {task.priority || 'Low'}
          </button>
        </div>
        <div className="task-content">{task.description}</div>
        <div className="assigned-contacts-board">
          {task.contacts && task.contacts.map(contact => (
            <div key={contact.id} className="contact-initials" style={{ backgroundColor: contact.color }}>
              {contact.name
                .split(' ')
                .map(part => part.charAt(0))
                .join('')
                .toUpperCase()}
            </div>
          ))}
        </div>
      </li>
    ));
  };

  return (
    <div className="kanban-board">
      {['todo', 'In Progress', 'awaiting Feedback', 'done'].map(category => (
        <div key={category} className="board-column">
          <div className="add-button" onClick={toggleTask}>
            <FontAwesomeIcon icon={faPlus} className="navbar-icon" />
            <p className="add-text">Add</p>
          </div>
          <h3 className="column-title">{category.replace(/([A-Z])/g, ' $1')}</h3>
          <ul className="task-list">
            {renderTasks(tasks[category])}
          </ul>
        </div>
      ))}
      <TaskDetailsPopup task={selectedTask} onClose={closeTaskDetails} /> {/* Popup-Komponente */}
      <Task show={showTask} onClose={toggleTask} createTask={createTask} />
    </div>
  );
};

export default Board;
