import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import './Summary.css'; // Importiere die CSS-Datei für das Styling

const Summary = () => {
  const [date, setDate] = useState(new Date());

  const getGreeting = () => {
    const currentTime = new Date().getHours();
    let greeting = '';

    if (currentTime < 12) {
      greeting = 'Good Morning';
    } else if (currentTime < 18) {
      greeting = 'Good Afternoon';
    } else {
      greeting = 'Good Evening';
    }

    return greeting;
  };

  return (
    <div className="summary-wrapper">
      <div className="summary-greeting">
        <h2>{getGreeting()}, John!</h2>
        <Calendar onChange={setDate} value={date} />
      </div>
      <div className="summary-container">
        <h2>Summary</h2>
        <h4>Everything in a Nutshell</h4>
        <div className="summary-grid">
          <Link to="/board" className="summary-item">
            <span className="summary-number">4</span>
            <span className="summary-label">Tasks in Board</span>
          </Link>
          <Link to="/board" className="summary-item">
            <span className="summary-number">2</span>
            <span className="summary-label">Tasks in Progress</span>
          </Link>
          <Link to="/board" className="summary-item">
            <span className="summary-number">1</span>
            <span className="summary-label">Awaiting Feedback</span>
          </Link>
          <Link to="/board" className="summary-item urgent">
            <span className="summary-number">1</span>
            <span className="summary-label">Urgent</span>
          </Link>
          <Link to="/board" className="summary-item">
            <span className="summary-deadline">28. Juni 2023</span>
            <span className="summary-label">Upcoming Deadline</span>
          </Link>
          <Link to="/board" className="summary-item">
            <span className="summary-number">1</span>
            <span className="summary-label">To-do</span>
          </Link>
          <Link to="/board" className="summary-item">
            <span className="summary-number">0</span>
            <span className="summary-label">Done</span>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default Summary;
