import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTimes } from '@fortawesome/free-solid-svg-icons';
import Datetime from 'react-datetime';
import 'react-datetime/css/react-datetime.css';

import './Task.css';  // Import der CSS-Datei

const Task = ({ show, onClose }) => {
    const categories = [
        { id: 1, name: 'To Do' },
        { id: 2, name: 'In Progress' },
        { id: 3, name: 'Awaiting Feedback' },
        { id: 4, name: 'Done' }
    ];

    const [dueDate, setDueDate] = useState(null); // State für Fälligkeitsdatum
    const [subtaskText, setSubtaskText] = useState(''); // State für Subtask-Text
    const [subtasks, setSubtasks] = useState([]); // State für Subtasks
    const [priority, setPriority] = useState('medium'); // State für Priorität

    const handleAddSubtask = () => {
        if (subtaskText.trim()) {
            setSubtasks([...subtasks, { text: subtaskText, completed: false }]);
            setSubtaskText('');
        }
    };

    const handleToggleSubtask = index => {
        const newSubtasks = subtasks.map((subtask, i) =>
            i === index ? { ...subtask, completed: !subtask.completed } : subtask
        );
        setSubtasks(newSubtasks);
    };

    const handlePriorityChange = selectedPriority => {
        setPriority(selectedPriority);
    };

    const handleSubmit = event => {
        event.preventDefault();
        // Hier kannst du die gesammelten Daten speichern oder weiterverarbeiten
        console.log("Form submitted with data:", {
            title: event.target.title.value,
            description: event.target.description.value,
            category: event.target.category.value,
            dueDate: dueDate,
            priority: priority,
            subtasks: subtasks
        });
    };

    return (
        <div className={`task-container ${show ? 'show' : ''}`}>
            <div className="task-header">
                <h2>Add Task</h2>
                <button className="close-button" onClick={onClose}>
                    <FontAwesomeIcon icon={faTimes} />
                </button>
            </div>
            <form className="task-form" onSubmit={handleSubmit}>
                <div className="form-left">
                    <label>Title</label>
                    <input type="text" name="title" placeholder="Enter a title" />
                    <label>Description</label>
                    <textarea name="description" placeholder="Enter a description"></textarea>
                    <label>Category</label>
                    <select name="category">
                        <option value="">Select task category</option>
                        {categories.map(category => (
                            <option key={category.id} value={category.id}>
                                {category.name}
                            </option>
                        ))}
                    </select>
                    <label>Assigned to</label>
                    <select>
                        <option>Contacts to assign</option>
                    </select>
                </div>
                <div className="form-right">
                    <label>Due date</label>
                    <Datetime
                        value={dueDate}
                        onChange={date => setDueDate(date)}
                        dateFormat="DD/MM/YYYY"
                        timeFormat={false}
                        inputProps={{ placeholder: 'Select due date' }}
                    />
                    <label>Prio</label>
                    <div className="prio-buttons">
                        <button
                            type="button"
                            className={`prio-button urgent ${priority === 'urgent' ? 'selected' : ''}`}
                            onClick={() => handlePriorityChange('urgent')}
                        >
                            Urgent
                        </button>
                        <button
                            type="button"
                            className={`prio-button medium ${priority === 'medium' ? 'selected' : ''}`}
                            onClick={() => handlePriorityChange('medium')}
                        >
                            Medium
                        </button>
                        <button
                            type="button"
                            className={`prio-button low ${priority === 'low' ? 'selected' : ''}`}
                            onClick={() => handlePriorityChange('low')}
                        >
                            Low
                        </button>
                    </div>
                    <label>Subtasks</label>
                    <div className="subtask-input">
                        <input
                            type="text"
                            value={subtaskText}
                            onChange={e => setSubtaskText(e.target.value)}
                            placeholder="Add new subtask"
                        />
                        <button type="button" onClick={handleAddSubtask}>Add</button>
                    </div>
                    <ul className="subtask-list">
                        {subtasks.map((subtask, index) => (
                            <li key={index} className={subtask.completed ? 'completed' : ''}>
                                <input
                                    type="checkbox"
                                    checked={subtask.completed}
                                    onChange={() => handleToggleSubtask(index)}
                                />
                                <span>{subtask.text}</span>
                            </li>
                        ))}
                    </ul>
                </div>
            </form>
            <div className="form-footer">
                <button type="button" className="clear-button">Clear</button>
                <button type="submit" className="create-button">Create Task</button>
            </div>
        </div>
    );
};

export default Task;
